import React, { useState } from "react";
import "./Work.css";
import Sidebar from "../../components/Sidebar/Sidebar";
import WORK from "../../assets/work";

export default function Work() {
  const [active, setActive] = useState(0);

  return (
    <div className="about">
      <Sidebar
        items={WORK.map((work, index) => (
          <Project {...work} onClick={() => console.log(index)} />
        ))}
      ></Sidebar>
      <div className="intro-container">
        <p className="heading">{WORK[active].name}</p>
        <br />
        <div className="project-images">
          {WORK[active].images.map((img, index) => (
            <img
              src={`images/${img}`}
              alt={`${WORK[active].name} number ${index}`}
              style={{ maxWidth: `${100 / WORK[active].images.length}%` }}
            />
          ))}
        </div>
        <br />
        <div
          dangerouslySetInnerHTML={{ __html: WORK[active].description }}
        ></div>
        <br />
        <br />
      </div>
    </div>
  );
}

function Project({ name, date, link, onClick }) {
  return (
    <div className="experience-container" onClick={onClick}>
      <div className="experience-header">
        <a href={link} target="_blank" rel="noreferrer">
          <b>{name}</b>
        </a>
        <p className="experience-dates">{date}</p>
      </div>
    </div>
  );
}
