import React from "react";
import "./About.css";
import Sidebar from "../../components/Sidebar/Sidebar";
import EXPERIENCE from "../../assets/experience";

export default function About() {
  return (
    <div className="about">
      <Sidebar
        items={EXPERIENCE.map((exp) => (
          <Experience
            {...exp}
            dates={[exp.dates.start, exp.dates.end ?? "Present"]}
          />
        ))}
      ></Sidebar>
      <div className="intro-container">
        <p className="heading">Hi, I'm Michelle.</p>
        <br />
        <p>
          I currently study Information Systems student at Carnegie Mellon
          University, with minors in Computer Science and Human-Computer
          Interaction. I'm originally from Maryland and am currently based in
          Pittsburgh.
        </p>
        <br />
        <p>
          I enjoy working on creative web tools and creating engaging
          experiences through technology. Currently, I'm interested in
        </p>
      </div>
    </div>
  );
}

function Experience({ name, dates, description, position, link }) {
  return (
    <div className="experience-container">
      <div className="experience-header">
        <a href={link} target="_blank" rel="noreferrer">
          <b>{name}</b>
        </a>
        <p className="experience-dates">{dates.join("-")}</p>
      </div>
      <p className="experience-position">{position}</p>
      <p>{description}</p>
    </div>
  );
}
